targets.expr:
sum(
  rate(container_cpu_usage_seconds_total{container!="POD", cluster=~"$cluster", node=~"$node", pod=~"$pod"}[1m])
) by (pod, cluster, node)

sum(
  rate(container_cpu_usage_seconds_total{container!="POD", cluster=~"$cluster", node=~"$node", pod=~"$pod"}[1m])
) by (pod, cluster, node)
/
sum(
  kube_pod_container_resource_limits_cpu_cores{container!="POD", cluster="$cluster", pod=~"$pod"}
) by (pod, cluster, node)

sum(
rate(container_memory_working_set_bytes{container!="POD", container!="", cluster=~"$cluster", node=~"$node", pod=~"$pod"}[1m])
) by (pod, cluster, node)
/
sum(
  kube_pod_container_resource_limits{container!="POD", cluster="$cluster", pod=~"$pod", resource="memory"}
) by (pod, cluster, node)

sum (
  rate(container_network_receive_bytes_total{container!="POD", cluster=~"$cluster", node=~"$node", pod=~"$pod"}[1m])
) by (pod, cluster, node)

sum (
  rate(container_network_transmit_bytes_total{container!="POD", cluster=~"$cluster", node=~"$node", pod=~"$pod"}[1m])
) by (pod, cluster, node)

sum (
  rate(container_network_receive_packets_dropped_total{container!="POD", pod!="", cluster=~"$cluster", node=~"$node", pod=~"$pod"}[1m])
) by (pod, cluster, node)

sum (
  rate(container_network_transmit_packets_dropped_total{container!="POD", pod!="", cluster=~"$cluster", node=~"$node", pod=~"$pod"}[1m])
) by (pod, cluster, node)

sum (
  rate(container_network_receive_errors_total{container!="POD", pod!="", cluster=~"$cluster", node=~"$node", pod=~"$pod"}[1m])
) by (pod, cluster, node)

sum (
  rate(container_network_transmit_errors_total{container!="POD", pod!="", cluster=~"$cluster", node=~"$node", pod=~"$pod"}[1m])
) by (pod, cluster, node)

(
  sum by (pod) (kube_pod_container_status_terminated{cluster=~"$cluster", pod=~"$pod"}) == 1
) * on(pod) group_left(reason) (
  sum by (pod, reason) (kube_pod_container_status_terminated_reason{cluster=~"$cluster", pod=~"$pod"}) == 1
)

(
  sum by (pod) (kube_pod_container_status_waiting{cluster=~"$cluster", pod=~"$pod"}) == 1
) * on(pod) group_left(reason) (
  sum by (pod, reason) (kube_pod_container_status_waiting_reason{cluster=~"$cluster", pod=~"$pod"}) == 1
)
