targets.expr:
count(
  count by (dest_server) (
    argocd_app_info 
    * on(name) group_left(label_environment) argocd_app_labels{label_environment=~"$environment", label_team=~"$team"}
  )
)

count(count by (repo) (
    argocd_app_info * on(name) group_left argocd_app_labels{label_environment=~"$environment", label_team=~"$team"}
))

sum(
    round(
        increase(
            argocd_app_sync_total[$interval]
            * on(name) group_left argocd_app_labels{label_environment=~"$environment", label_team=~"$team"}
        )
    )
) by (name)

sum(
    round(
        increase(
            argocd_app_sync_total{phase=~"Error|Failed"}[$interval]
            * on(name) group_left argocd_app_labels{label_environment=~"$environment", label_team=~"$team"}
        )
    )
) by (name, phase)
